                   :status="publish.processStatus"/>
    </div>
    <publish :class="['publish-info-drawer', 'hidden-publish-base', publish.hidden ? 'hidden-publish':'publish-info-drawer-in']"
             v-show="publish.toPublishVideo"
             v-if="publish.disable"
             :from-page="false"
             @publish-before="handlePublishBefore"
             @close-publish="handlePublishClose"
             @close-publish-process="publishProcess"/>
  </div>
</template>

<script>
  import VideoHead from "./assembly/VideoHead";
  import SimpleVideo from "./assembly/SimpleVideo";
  import VideoDescription from "./assembly/VideoDescription";
  import VideoFoot from "./assembly/VideoFoot";
  import Top from "../Top";
  import {getVideoByRecommended} from "@/api/video/query";
  import Publish from "./publish/publish";

  export default {
    name: "index",
    components: {Publish, Top, VideoDescription, SimpleVideo, VideoHead, VideoFoot},
    data() {
      return {
        betaInfo: {
          x: window.innerWidth - 240, // 默认位置（右侧）
          y: 100 // 距离顶部20px
        },

        inited: false,

        count: 0,
        keyMaster: 1,
        keySlave: -1,
        videoSoundSwitch: false, //初始进入时静音播放
        videos: [],
        currentIndex: 0,    // 当前播放视频的索引
        slaveIndex: 1,
        translateY: 0,      // Y轴平移距离
        startY: 0,          // 触摸开始时的 Y 坐标
        isSnapping: false,  // 是否在对齐过程中
        topSlave: -9999,
        topMaster: 0,
        isUp: false,
        who: 'master',
        inChange: false,

        footHeight: 0,

        publish: {
          toPublishVideo: false,
          showProcess: false,
          percentage: 0,
          hidden: true,
          processStatus: null,
          disable: true
        }
      }
    },
    methods: {
      moveVideo() {

      },
      publishProcess(num) {
        this.publish.percentage = num;
        if (num >= 100) {
          this.publish.processStatus = 'success';
          setTimeout(() => {
            this.publish.showProcess = false;
            this.publish.percentage = 0;
            this.publish.hidden = true;
            this.publish.toPublishVideo = false;
            this.publish.disable = false;
          }, 3000);
        }
      },
      handlePublishOpen() {
        setTimeout(() => {this.pause();}, 100);
        this.publish.disable = true;
        this.publish.processStatus = null;
        this.publish.hidden = false;
        this.publish.toPublishVideo = true;
      },
      handlePublishBefore() {
        this.publish.toPublishVideo = false;
        this.publish.hidden = true;
        this.publish.showProcess = true;
        setTimeout(() => {this.play();}, 100);
      },
      handlePublishClose(showProcess) {
        this.publish.toPublishVideo = false;
        this.publish.hidden = true;
        this.publish.showProcess = showProcess;
        this.publish.disable = false;
        setTimeout(() => {this.play();}, 100);
      },
      queryVideoList(queryCount) {
        let needQuery = false;
        let index = 0;
        if (this.who === 'master') {
          index = this.currentIndex;
        } else {
          index = this.slaveIndex;
        }
        needQuery = (this.videos.length - index) < 10;
        if (!this.isUp) {
          if (index === 0) {
            this.findVideo(20);
          }
        } else {
          if (needQuery) {
            getVideoByRecommended(this.videoIdsAsString(), queryCount)
              .then(res => {
                if (res.code === 200) {
                  let data = res.data;
                  if (data) {
                    data.forEach(item => this.videos.push(this.fixVideoUrl(item)));
                  }
                }
              });
          }
        }

      },

      openSound(value) {
        this.videoSoundSwitch = value;
        if (this.videoSoundSwitch) {
          this.$refs.videoMaster.openSound();
          this.$refs.videoSlave.openSound();
        }
      },
      // 触摸开始时记录初始坐标
      startTouch(event) {
        this.inChange = true;
        this.startY = event.touches[0].clientY;
        this.isSnapping = false;
      },
      // 滑动时更新 translateY 让视频随手势滑动
      moveTouch(event) {
        let deltaY = event.touches[0].clientY - this.startY;

        if (deltaY > window.innerHeight) deltaY = window.innerHeight;

        if (deltaY < -window.innerHeight) deltaY = -window.innerHeight;

        this.translateY = deltaY;

        let slaveTop = 0;
        if (deltaY > 0) {
          slaveTop = -window.innerHeight + deltaY;
        } else if (deltaY < 0) {
          slaveTop = window.innerHeight + deltaY;
        } else {
          slaveTop = window.innerHeight;
        }

        let judgeIndex = this.who === 'master' ? this.currentIndex : this.slaveIndex;
        if (judgeIndex >= this.videos.length - 1 && deltaY < 0) {
          if (this.who === 'master') {
            this.topMaster = deltaY;
          }
          if (this.who === 'slave') {
            this.topSlave = deltaY;
          }
        } else if (judgeIndex <= 0 && deltaY > 0) {
          if (this.who === 'master') {
            this.topMaster = deltaY;
          }
          if (this.who === 'slave') {
            this.topSlave = deltaY;
          }
        } else {
          this.topMaster = this.who === 'master' ? deltaY : slaveTop;
          this.topSlave = this.who === 'master' ? slaveTop : deltaY;
        }

        if (deltaY < 0) {
          if (this.who === 'master') {
            if (this.slaveIndex !== this.currentIndex + 1) {
              this.slaveIndex = this.currentIndex + 1;
            }
          } else {
            if (this.currentIndex !== this.slaveIndex + 1) {
              this.currentIndex = this.slaveIndex + 1;
            }
          }
        } else {
          if (this.who === 'master') {
            if (this.slaveIndex !== this.currentIndex - 1) {
              this.slaveIndex = this.currentIndex - 1;
            }
          }
          if (this.who === 'slave') {
            if (this.currentIndex !== this.slaveIndex - 1) {
              this.currentIndex = this.slaveIndex - 1;
            }
          }
        }

      },
      // 结束滑动，判断滑动距离并决定是否切换视频
      endTouch(event) {
        this.isSnapping = true;
        let deltaY = event.changedTouches[0].clientY - this.startY;

        if (deltaY === 0) {
          console.log("un move")
          return;
        }

        this.isUp = deltaY < 0;
        const threshold = window.innerHeight / 5;  // 切换视频的滑动阈值
        const thresholdM = window.innerHeight / 10;  // 切换视频的滑动阈值

        let judgeIndex = this.who === 'master' ? this.currentIndex : this.slaveIndex;
        if (this.translateY < -threshold && judgeIndex < this.videos.length - 1) {
          this.func();
          this.snapToVideo();
        } else if (this.translateY > threshold && judgeIndex > 0) {
          this.func();
          this.snapToVideo();
        } else if (this.translateY < -thresholdM || this.translateY > thresholdM) {

          // } else {
          if (this.isUp) {
            if (judgeIndex < this.videos.length - 1) {
              this.topMaster = this.who === 'master' ? 0 : window.innerHeight;
              this.topSlave = this.who === 'master' ? window.innerHeight : 0;
            } else {
              if (this.who === 'master') {
                this.topMaster = 0;
              }
              if (this.who === 'slave') {
                this.topSlave = 0;
              }
            }
          } else {
            if (judgeIndex > 0) {
              this.topMaster = this.who === 'master' ? 0 : -window.innerHeight;
              this.topSlave = this.who === 'master' ? -window.innerHeight : 0;
            } else {
              if (this.who === 'master') {
                this.topMaster = 0;
              }
              if (this.who === 'slave') {
                this.topSlave = 0;
              }
            }
          }
          this.inChange = false;
        }
      },
      func() {
        if (this.who === 'master') {
          this.$refs.videoMaster.doClose();
        } else {
          this.$refs.videoSlave.doClose();
        }
        this.who = (this.who === 'master' ? 'slave' : 'master');
        if (this.isUp) {
          this.topMaster = this.who === 'master' ? 0 : -window.innerHeight;
          this.topSlave = this.who === 'master' ? -window.innerHeight : 0;
        } else {
          this.topMaster = this.who === 'master' ? 0 : window.innerHeight;
          this.topSlave = this.who === 'master' ? window.innerHeight : 0;
        }
      },
      // 鼠标滚轮切换视频
      handleWheel(event) {
        if (this.inChange) return;
        this.inChange = true;
        let judgeIndex = this.who === 'master' ? this.currentIndex : this.slaveIndex;
        let deltaY = event.deltaY;
        this.isUp = deltaY > 0;
        if (this.isUp && judgeIndex <= 0) {
          return
        }
        if (!this.isUp && judgeIndex >= this.videos.length) {
          return;
        }

        if (deltaY < 0) {
          if (this.who === 'master') {
            if (this.currentIndex < this.videos.length - 1 && this.slaveIndex !== this.currentIndex + 1) {
              this.slaveIndex = this.currentIndex + 1;
            }
          } else {
            if (this.slaveIndex < this.videos.length - 1 && this.currentIndex !== this.slaveIndex + 1) {
              this.currentIndex = this.slaveIndex + 1;
            }
          }
        } else {
          if (this.who === 'master') {
            if (this.currentIndex > 0 && this.slaveIndex !== this.currentIndex - 1) {
              this.slaveIndex = this.currentIndex - 1;
            }
          }
          if (this.who === 'slave') {
            if (this.slaveIndex > 0 && this.currentIndex !== this.slaveIndex - 1) {
              this.currentIndex = this.slaveIndex - 1;
            }
          }
        }
        this.func();
        this.snapToVideo();
        setTimeout(() => {
          this.inChange = false;
        }, 1500);
      },
      // 向下滑动切换到上一个视频
      handleSwipeDown() {
        this.isUp = true;
        this.handelKey(false);
      },
      // 向上滑动切换到下一个视频
      handleSwipeUp() {
        this.isUp = false;
        this.handelKey(true);
      },
      handelKey(isUp) {
        this.inChange = true;
        let judgeIndex = this.who === 'master' ? this.currentIndex : this.slaveIndex;
        if (isUp && judgeIndex <= 0) {
          return
        }
        if (!isUp && judgeIndex >= this.videos.length) {
          return;
        }

        if (isUp) {
          if (this.who === 'master') {
            if (this.currentIndex > 0 && this.slaveIndex !== this.currentIndex - 1) {
              this.slaveIndex = this.currentIndex - 1;
            }
          } else {
            if (this.slaveIndex > 0 && this.currentIndex !== this.slaveIndex - 1) {
              this.currentIndex = this.slaveIndex - 1;
            }
          }
        } else {
          if (this.who === 'master') {
            if (this.currentIndex < this.videos.length - 1 && this.slaveIndex !== this.currentIndex + 1) {
              this.slaveIndex = this.currentIndex + 1;
            }
          }
          if (this.who === 'slave') {
            if (this.slaveIndex < this.videos.length - 1 && this.currentIndex !== this.slaveIndex + 1) {
              this.currentIndex = this.slaveIndex + 1;
            }
          }
        }
        this.func();
        this.snapToVideo();
        this.inChange = false;
      },
      // 自动对齐到视频位置
      snapToVideo() {
        // this.topMaster = 0;
        // this.topSlave = window.innerHeight;
        this.queryVideoList(2);

        this.$refs.videoContainBox.style.height = (window.innerHeight - this.$refs.footBox.clientHeight) + 'px';
        this.$nextTick(() => {
          this.updateWindowHeight();
          if (this.who === 'master') {
            const video = this.$refs.videoMaster;
            if (video) {
              video.doInit();
            }
          } else {
            const video = this.$refs.videoSlave;
            if (video) {
              video.doInit();
            }
          }
        });
      },
      async findVideo(count) {
        await getVideoByRecommended(this.videoIdsAsString(), count).then(res => {
          if (res.code === 200 && res.data.length > 0) {
            this.videos = this.fixVideoUrl(res.data);
            this.inited = true;
          }
        });
      },
      updateWindowHeight() {
        this.$refs.videoContainBox.style.height = (window.innerHeight - this.$refs.footBox.clientHeight) + 'px';
        if (this.who === 'master') {
          this.$refs.videoMaster.resizeVideo();
        } else {
          this.$refs.videoSlave.resizeVideo();
        }
      },
      videoIdsAsString() {
        if (this.videos && this.videos.length > 0) {
          return this.videos.map(item => item.currentVideoIndex).join(',');
        }
        return '';
      },
      fixVideoUrl(videoArr) {
        if (!(videoArr instanceof Array)) {
          videoArr.about.authInfo.url = process.env.VUE_APP_BASE_API + videoArr.about.authInfo.url;
          return videoArr;
        }

        for (let i = 0; i < videoArr.length; i++) {
          videoArr[i].about.authInfo.url = process.env.VUE_APP_BASE_API + videoArr[i].about.authInfo.url;
        }
        return videoArr;
      },
      pause() {
        this.getCurrentVideo().clickToPauseVideo();
      },
      play() {
        this.getCurrentVideo().clickToPlayVideo();
      },
      getCurrentVideo() {
        return this.who === 'master' ? this.$refs.videoMaster : this.$refs.videoSlave;
      },

      flush(type) {

      }
    },
    created() {
      //this.findVideo(20);
    },
    mounted() {
      this.footHeight = this.$refs.footBox.clientHeight;
      this.findVideo(20);
      document.addEventListener('touchmove', function (event) {
        if (window.scrollY === 0 && event.touches[0].clientY > 0) {
          event.preventDefault();
        }
      }, {passive: false});
      if (null != this.videos && this.videos.length > 0) {
        this.updateWindowHeight();
      }
      window.addEventListener('resize', this.updateWindowHeight);
    },
    beforeDestroy() {
      window.removeEventListener('resize', this.updateWindowHeight);
    }
  }
</script>

